	/*
	 * Utils
	 */
	
	public double scaleTo05(double v)
	{
		return v < 0 ? Math.abs(v)/2 : v/2 + 0.5;
	}
	
	public double scaleFrom05(double v)
	{
		return v < 0.5 ? - (v * 2): (v - 0.5) * 2;
	}
